let tasks = [];
    
    function addTask() {
      const taskInput = document.getElementById('task-input');
      const prioritySelect = document.getElementById('priority-select');
      const reminderTime = document.getElementById('reminder-time');
      
      if (taskInput.value.trim() === '') return;
    
      const task = {
        id: Date.now(),
        title: taskInput.value,
        priority: prioritySelect.value,
        status: 'pending',
        createdAt: new Date(),
        reminder: reminderTime.value ? new Date(reminderTime.value) : null
      };
    
      tasks.push(task);
      taskInput.value = '';
      reminderTime.value = '';
      
      if (task.reminder) {
        setReminder(task);
      }
      
      renderTasks();
      updateStats();
    }
    
    function setReminder(task) {
      const now = new Date();
      const reminderTime = new Date(task.reminder);
      
      if (reminderTime > now) {
        const timeUntilReminder = reminderTime - now;
        setTimeout(() => {
          showNotification(`Reminder: ${task.title}`);
        }, timeUntilReminder);
      }
    }
    
    function showNotification(message) {
      const notification = document.getElementById('alarm-notification');
      const messageElement = document.getElementById('alarm-message');
      messageElement.textContent = message;
      notification.style.display = 'block';
      
      // Play notification sound
      const audio = new Audio('data:audio/wav;base64,');
      audio.play();
      
      setTimeout(() => {
        notification.style.display = 'none';
      }, 5000);
    }
    
    function deleteTask(id) {
      tasks = tasks.filter(task => task.id !== id);
      renderTasks();
      updateStats();
    }
    
    function toggleComplete(id) {
      const task = tasks.find(task => task.id === id);
      if (task) {
        task.status = task.status === 'completed' ? 'pending' : 'completed';
        renderTasks();
        updateStats();
      }
    }
    
    function filterTasks() {
      const priorityFilter = document.getElementById('filter-priority').value;
      const statusFilter = document.getElementById('filter-status').value;
      renderTasks(priorityFilter, statusFilter);
    }
    
    function renderTasks(priorityFilter = 'all', statusFilter = 'all') {
      const taskList = document.getElementById('task-list');
      taskList.innerHTML = '';
    
      let filteredTasks = tasks;
      
      if (priorityFilter !== 'all') {
        filteredTasks = filteredTasks.filter(task => task.priority === priorityFilter);
      }
      
      if (statusFilter !== 'all') {
        filteredTasks = filteredTasks.filter(task => task.status === statusFilter);
      }
    
      filteredTasks.forEach(task => {
        const taskCard = document.createElement('div');
        taskCard.className = `task-card ${task.status}`;
        
        taskCard.innerHTML = `
          <div class="task-info">
            <div class="task-title">${task.title}</div>
            <div class="task-meta">
              <span class="priority ${task.priority}">${task.priority}</span>
              <span>${new Date(task.createdAt).toLocaleDateString()}</span>
              ${task.reminder ? `<span>‚è∞ ${new Date(task.reminder).toLocaleString()}</span>` : ''}
            </div>
          </div>
          <div class="actions">
            <button class="complete-btn" onclick="toggleComplete(${task.id})">
              ${task.status === 'completed' ? 'Undo' : 'Complete'}
            </button>
            <button class="delete-btn" onclick="deleteTask(${task.id})">Delete</button>
          </div>
        `;
    
        if (task.status === 'completed') {
          taskCard.style.opacity = '0.7';
        }
    
        taskList.appendChild(taskCard);
      });
    }
    
    function updateStats() {
      const stats = document.getElementById('task-stats');
      const total = tasks.length;
      const completed = tasks.filter(task => task.status === 'completed').length;
      const pending = total - completed;
    
      stats.innerHTML = `
        <div style="font-size: 0.9rem; color: var(--gray)">
          Total: ${total} | Completed: ${completed} | Pending: ${pending}
        </div>
      `;
    }
    
    // Check for existing reminders on page load
    function initializeReminders() {
      const now = new Date();
      tasks.forEach(task => {
        if (task.reminder && new Date(task.reminder) > now) {
          setReminder(task);
        }
      });
    }
    
    // Initial render
    renderTasks();
    updateStats();
    initializeReminders();